query GetFirst100UsersLevel3 {
  levelClaims(
    where: {level: 3}
    orderBy: timestamp
    orderDirection: asc
    first: 100
  ) {
    account {
      id
    }
    level
    timestamp
  }
}

1cd45e38746493eef15f98883c8ad11905436d2f:
query GetUserBadges($user: String!) {
  badges {
    badgeId
    uri
    perks {
      id
      tier
      isCompleted
    }
    badgeTiers(orderBy: tier, orderDirection: asc) {
      points
      tier
      uri
    }
  }
  accountBadges(where: {user: $user}) {
    points
    tier
    badge {
      badgeId
      uri
      perks {
        id
        tier
        isCompleted
      }
      badgeTiers(orderBy: tier, orderDirection: asc) {
        points
        tier
        uri
        perk {
          id
          badgeId
          tier
          token
          amount
          maxClaims
          totalClaims
          isCompleted
        }
      }
    }
  }
}
